import React, { useEffect } from 'react';
import ButtonElement from '../../../../Components/ButtonElement';
import { Card, CardBody } from '../../../../Components/Card';
import SVGIcons from '../../../../Components/SVGIcons';
import style from '../../style.module.scss';
import { getVideoLink, getVideoLinks } from './Creators.utils';
import User from '../../../../../public/img/user.png';

export default function Creators({
  creators,
  videos,
  getMoreCreators,
  showMore = false,
  clickedShowMore = false,
  loading = false,
  stopShowMore,
}) {
  return (
    <div className={style.outputcards}>
      {creators.map((creator) => (
        <CreatorCard creator={creator} videos={videos} />
      ))}
      {showMore && !clickedShowMore ? (
        <ButtonElement onClick={getMoreCreators}>
          View More Channels
        </ButtonElement>
      ) : null}
      {clickedShowMore && loading && !stopShowMore ? 'Loading..' : null}
    </div>
  );
}

function CreatorCard({ creator, videos }) {
  return (
    <Card className={style.outputCard}>
      <CardBody>
        <div className={style.imgContainer}>
          <img
            src={creator.snippet?.thumbnails?.medium?.url || User}
            className={style.creatorImg}
          />
        </div>
        <div className={style.creatorInfo}>
          <a href='/'>
            <span>{creator.title || 'Unknown'}</span>
            <span className={style.creatorStatus}>
              <SVGIcons
                type='SVG-tick'
                color={creator.user ? 'green' : 'gray'}
              />
            </span>
          </a>
        </div>
        <div className={style.creatorInfo}>
          {getVideoLinks(videos, creator.id).length ? (
            getVideoLinks(videos, creator.id).map((video) => (
              <a href={getVideoLink(item.yt_video_id)}>
                <p>{video.title}</p>
              </a>
            ))
          ) : (
            <p>No Videos</p>
          )}
          {/* <a>
          <p>Video 1</p>
          </a>
          <a>
          <p>Video 2</p>
          </a>
          <a>
          <p>Video 3</p>
          </a> */}
        </div>
      </CardBody>
    </Card>
  );
}
